mapper.select:


SELECT *
FROM (
	SELECT t.*, ROWNUM rn
	FROM (
		select
			cp_list_id, cp_rate, cp_successrate, cp_defectrate,
			cp_id
		from v3_cp_list


				and cp_id = #{filter.cp_id}


				and wo.work_order_date between #{filter.fromDate} and #{filter.toDate}


	) t

	WHERE ROWNUM <= #{endRow}

)

WHERE rn >= #{startRow}

SELECT COUNT(*)
from v3_cp_list


		and cp_id = #{filter.cp_id}


		and wo.work_order_date between #{filter.fromDate} and #{filter.toDate}

mapper.delete:
delete from v3_cp_list
where cp_list_id in

	#{id}
