mapper.select:
SELECT * FROM ( SELECT t.*, ROWNUM rn FROM ( select i.inspection_result_id, i.inspection_result_date, i.inspection_result_good, i.inspection_result_bad, i.worker_id, i.stock_id, w.worker_name, it.item_name from v3_inspection_result i left outer join v3_worker w on i.worker_id = w.worker_id left outer join v3_stock s on i.stock_id = s.stock_id left outer join v3_item it on s.item_id = it.item_id
inspection_type = 0
and lower(i.inspection_result_id) like lower('%' || #{filter.inspection_result_id} || '%')
and i.inspection_result_date BETWEEN #{filter.fromDate} AND #{filter.toDate}
and lower(it.item_name) like lower('%' || #{filter.item_name} || '%')
order by inspection_result_id ) t
WHERE ROWNUM <= #{endRow}
)
WHERE rn >= #{startRow}

mapper.delete:
delete from v3_inspection_result
where inspection_result_id in

	#{id}
